import React, { useState, useEffect } from 'react';

const ProgressBar = ({ videoRef }) => {
    const [currentTime, setCurrentTime] = useState(0);
    const [duration, setDuration] = useState(0);

    useEffect(() => {
        const handleTimeUpdate = () => {
            if (videoRef?.current) {
                setCurrentTime(videoRef.current.currentTime);
            }
        };

        const handleLoadedMetadata = () => {
            if (videoRef?.current) {
                setDuration(videoRef.current.duration);
            }
        };

        if (videoRef?.current) {
            videoRef.current.addEventListener('timeupdate', handleTimeUpdate);
            videoRef.current.addEventListener('loadedmetadata', handleLoadedMetadata);
        }

        return () => {
            if (videoRef?.current) {
                videoRef.current.removeEventListener('timeupdate', handleTimeUpdate);
                videoRef.current.removeEventListener('loadedmetadata', handleLoadedMetadata);
            }
        };
    }, [videoRef]);

    const handleSeek = (time) => {
        if (videoRef?.current) {
            videoRef.current.currentTime += time;
        }
    };

    return (
        <div className="progress-bar">
            <div className="progress-bar-inner" style={{ width: `${(currentTime / duration) * 100}%` }}></div>
            <div className="progress-bar-controls">
                <button className="seek-button" onClick={() => handleSeek(-10)}>Backward</button>
                <button className="seek-button" onClick={() => handleSeek(10)}>Forward</button>
            </div>
        </div>
    );
};

export default ProgressBar;
