import React, { useRef, useEffect, useCallback, useState } from 'react';
import 'shaka-player/dist/controls.css';
import shaka from 'shaka-player/dist/shaka-player.ui.js';

const VideoPlayer = () => {
	const videoComponent = useRef(null);
	const videoContainer = useRef(null);
	const progressRef = useRef(null);
	const [isPlaying, setIsPlaying] = useState(false);

	const onError = useCallback((error) => {
		console.error('Error code', error.code, 'object', error);
	}, []);

	const onErrorEvent = useCallback((event) => {
		onError(event.detail);
	}, [onError]);

	const handleSkipForward = () => {
		const video = videoComponent.current;
		video.currentTime += 10;
	};

	const handleSkipBackward = () => {
		const video = videoComponent.current;
		video.currentTime -= 10;
	};

	const handleProgressBarClick = (event) => {
			const video = videoComponent.current;
			const progress = progressRef.current;
			const progressWidth = progress.offsetWidth;
			const clickPosition = event.clientX - progress.getBoundingClientRect().left;
			const clickPercentage = (clickPosition / progressWidth) * 100;
			const seekTime = (clickPercentage * video.duration) / 100;

			if (isFinite(seekTime)) {
				video.currentTime = seekTime;
			}
	};

	useEffect(() => {
		const manifestUri = 'https://storage.googleapis.com/shaka-demo-assets/angel-one/dash.mpd';
		const video = videoComponent.current;
		// const container = videoContainer.current;

		let player = new shaka.Player(video);
		const uiConfig = {};
		uiConfig.controlPanelElements = [];

		// const ui = new shaka.ui.Overlay(player, container, video);
		// ui.configure(uiConfig);
		// ui.getControls();

		player.addEventListener('error', onErrorEvent);

		player.load(manifestUri)
			.then(() => {
				console.log('The video has now been loaded!');
			})
			.catch(onError);

		return () => {
			player.removeEventListener('error', onErrorEvent);
			player.destroy();
			// ui.destroy();
		};
	}, [onErrorEvent, onError]);

	const handleVideoPlay = () => {
		setIsPlaying(true);
	};

	const handleVideoPause = () => {
		setIsPlaying(false);
	};

	return (
		<div className="video-container" ref={videoContainer}>
			<video
				className="shaka-video"
				ref={videoComponent}
				onClick={isPlaying ? handleVideoPause : handleVideoPlay}
				autoplay
			/>
			<div className="controls-container">
				<div className="progress-bar" onClick={handleProgressBarClick}>
					<div className="progress" ref={progressRef} />
				</div>
				<div className="skip-buttons">
					<button className="skip-button" onClick={handleSkipBackward}>
						- 10s
					</button>
					<button className="skip-button" onClick={handleSkipForward}>
						+ 10s
					</button>
				</div>
			</div>
		</div>
	);
};

export default VideoPlayer;
