import React, { useRef, useState, useEffect, useCallback } from 'react';
import debounce from 'lodash.debounce';

const ProgressBar = ({ videoRef }) => {
    const progressRef = useRef(null);
    const scrubberRef = useRef(null);
    const [isScrubbing, setIsScrubbing] = useState(false);

    const handleScrubberMouseMove = useCallback(
        debounce((event) => {
            if (isScrubbing) {
                const progressWidth = progressRef.current.offsetWidth;
                const clickPosition = event.clientX - progressRef.current.getBoundingClientRect().left;
                const clickPercentage = (clickPosition / progressWidth) * 100;
                const seekTime = (clickPercentage * videoRef.current.duration) / 100;

                videoRef.current.currentTime = seekTime;
            }
        }, 200),
        [isScrubbing]
    );

    useEffect(() => {
        const progressElement = progressRef.current;
        const scrubberElement = scrubberRef.current;

        const handleScrubberMouseDown = () => {
            setIsScrubbing(true);
            videoRef.current.pause();
        };

        const handleScrubberMouseUp = () => {
            setIsScrubbing(false);
            videoRef.current.play();
        };

        scrubberElement.addEventListener('mousedown', handleScrubberMouseDown);
        scrubberElement.addEventListener('mouseup', handleScrubberMouseUp);
        scrubberElement.addEventListener('mousemove', handleScrubberMouseMove);

        return () => {
            scrubberElement.removeEventListener('mousedown', handleScrubberMouseDown);
            scrubberElement.removeEventListener('mouseup', handleScrubberMouseUp);
            scrubberElement.removeEventListener('mousemove', handleScrubberMouseMove.cancel);
        };
    }, [videoRef, handleScrubberMouseMove]);

    const updateProgressBar = useCallback(() => {
        const progressElement = progressRef.current;
        const scrubberElement = scrubberRef.current;

        const progressPercentage = (videoRef.current.currentTime / videoRef.current.duration) * 100;
        const scrubberPosition = `${progressPercentage}%`;

        progressElement.style.width = scrubberPosition;
        scrubberElement.style.left = scrubberPosition;
    }, [videoRef]);

    useEffect(() => {
        const video = videoRef.current;
        video.addEventListener('timeupdate', updateProgressBar);

        return () => {
            video.removeEventListener('timeupdate', updateProgressBar);
        };
    }, [videoRef, updateProgressBar]);

    return (
        <div className="progress-bar">
            <div className="progress" ref={progressRef} />
            <div className="scrubber" ref={scrubberRef} />
        </div>
    );
};

export default ProgressBar;
