import React, { useRef, useEffect, useCallback, useState } from 'react';
import shaka from 'shaka-player/dist/shaka-player.ui.js';
import ProgressBar from './ProgressBar';
import SkipButtons from './SkipButtons';

import 'shaka-player/dist/controls.css'; // Import only necessary styles from the Shaka Player controls

const VideoPlayer = () => {
	const videoRef = useRef(null);
	const playerRef = useRef(null);

	const [isPlaying, setIsPlaying] = useState(false);

	const onError = useCallback((error) => {
		console.error('Error code', error.code, 'object', error);
	}, []);

	const onErrorEvent = useCallback(
		(event) => {
			onError(event.detail);
		},
		[onError]
	);

	useEffect(() => {
		const initializePlayer = async () => {
			shaka.polyfill.installAll();
			if (!shaka.Player.isBrowserSupported()) {
				console.error('Browser does not support Shaka Player');
				return;
			}
			const player = new shaka.Player(videoRef.current);
			playerRef.current = player;

			// Attach error event listener
			player.addEventListener('error', onErrorEvent);

			// Configure adaptive streaming
			player.configure({
				abr: {
					defaultBandwidthEstimate: 500000, // Example: 500 Kbps
				},
			});

			const manifestUrl =
				'https://storage.googleapis.com/shaka-demo-assets/angel-one/dash.mpd';

			try {
				await player.load(manifestUrl);
				console.log('The video has now been loaded!');
				setIsPlaying(true); // Autoplay the video
				videoRef.current.play();
			} catch (error) {
				onError(error);
			}
		};

		initializePlayer();

		return () => {
			if (playerRef.current) {
				// Clean up the player and event listeners
				playerRef.current.removeEventListener('error', onErrorEvent);
				playerRef.current.destroy();
			}
		};
	}, [onErrorEvent]);

	const handleVideoPlay = useCallback(() => {
		if (!isPlaying) {
			setIsPlaying(true);
			videoRef.current.play();
		}
	}, [isPlaying]);

	const handleVideoPause = useCallback(() => {
		if (isPlaying) {
			setIsPlaying(false);
			videoRef.current.pause();
		}
	}, [isPlaying]);

	return (
		<div className="video-container">
			<video
				className="shaka-video"
				ref={videoRef}
				onClick={isPlaying ? handleVideoPause : handleVideoPlay}
			/>
			<div className="controls-container">
				<ProgressBar videoRef={videoRef} />
				<SkipButtons videoRef={videoRef} />
			</div>
		</div>
	);
};

export default VideoPlayer;
