import React, { useRef, useEffect, useState, useCallback } from 'react';
import throttle from 'lodash.throttle';

const ProgressBar = ({ videoRef }) => {
    const innerProgressRef = useRef(null);
    const scrubberRef = useRef(null);
    const [isScrubbing, setIsScrubbing] = useState(false);

    const updateProgressBar = useCallback(
        throttle(() => {
            const innerProgress = innerProgressRef.current;
            const scrubber = scrubberRef.current;
            const currentTime = videoRef.current.currentTime;
            const duration = videoRef.current.duration;
            const progressPercentage = (currentTime / duration) * 100;

            innerProgress.style.width = `${progressPercentage}%`;
            scrubber.style.left = `${progressPercentage}%`;
        }, 200),
        [videoRef]
    );

    const handleProgressBarClick = (event) => {
        const progress = event.currentTarget;
        const progressWidth = progress.offsetWidth;
        const clickPosition = event.clientX - progress.getBoundingClientRect().left;
        const clickPercentage = (clickPosition / progressWidth) * 100;
        const seekTime = (clickPercentage * videoRef.current.duration) / 100;

        videoRef.current.currentTime = seekTime;
    };

    const handleScrubberMouseDown = () => {
        setIsScrubbing(true);
    };

    const handleMouseMove = (event) => {
        if (!isScrubbing) return;

        const progress = event.currentTarget;
        const progressWidth = progress.offsetWidth;
        const clickPosition = event.clientX - progress.getBoundingClientRect().left;
        const clickPercentage = (clickPosition / progressWidth) * 100;
        const seekTime = (clickPercentage * videoRef.current.duration) / 100;

        videoRef.current.currentTime = seekTime;
    };

    const handleMouseUp = () => {
        setIsScrubbing(false);
    };

    useEffect(() => {
        const video = videoRef.current;

        video.addEventListener('timeupdate', updateProgressBar);

        return () => {
            video.removeEventListener('timeupdate', updateProgressBar);
        };
    }, [videoRef, updateProgressBar]);

    useEffect(() => {
        const progress = innerProgressRef.current;
        const scrubber = scrubberRef.current;
        const progressContainer = progress.parentElement;

        progressContainer.addEventListener('mousemove', handleMouseMove);
        progressContainer.addEventListener('mouseup', handleMouseUp);

        return () => {
            progressContainer.removeEventListener('mousemove', handleMouseMove);
            progressContainer.removeEventListener('mouseup', handleMouseUp);
        };
    }, [handleMouseMove, handleMouseUp]);

    return (
        <div className="progress-bar" onClick={handleProgressBarClick}>
            <div className="progress">
                <div className="inner-progress" ref={innerProgressRef}></div>
                <div
                    className="scrubber"
                    ref={scrubberRef}
                    onMouseDown={handleScrubberMouseDown}
                ></div>
            </div>
        </div>
    );
};

export default ProgressBar;
