import React, { useRef, useEffect, useCallback, useState } from 'react';
import 'shaka-player/dist/controls.css';
import shaka from 'shaka-player/dist/shaka-player.ui.js';
import ProgressBar from "./ProgressBar";
import SkipButtons from "./SkipButtons";

const VideoPlayer = () => {
	const videoRef = useRef(null);
	const playerRef = useRef(null);

	const progressRef = useRef(null);
	const [isPlaying, setIsPlaying] = useState(false);

	const onError = useCallback((error) => {
		console.error('Error code', error.code, 'object', error);
	}, []);

	const onErrorEvent = useCallback((event) => {
		onError(event.detail);
	}, [onError]);

	const handleSkipForward = () => {
		const video = videoRef.current;
		video.currentTime += 10;
	};

	const handleSkipBackward = () => {
		const video = videoRef.current;
		video.currentTime -= 10;
	};

	const handleProgressBarClick = (event) => {
		const progress = progressRef.current;
		const progressWidth = progress.offsetWidth;
		const clickPosition = event.clientX - progress.getBoundingClientRect().left;
		const clickPercentage = (clickPosition / progressWidth) * 100;
		const seekTime = (clickPercentage * videoRef.current.duration) / 100;
		console.log(progress, progressWidth, clickPosition, clickPercentage, seekTime, videoRef.current.duration)
		videoRef.current.currentTime = seekTime;
	};

	useEffect(() => {
		// Initialize Shaka Player
		shaka.polyfill.installAll();
		if (!shaka.Player.isBrowserSupported()) {
			console.error('Browser does not support Shaka Player');
			return;
		}
		let player = new shaka.Player(videoRef.current);
		playerRef.current = player;

		// Attach error event listener
		player.addEventListener('error', onErrorEvent);

		// Configure adaptive streaming
		player.configure({
			abr: {
				// Set the default video quality
				defaultBandwidthEstimate: 500000, // Example: 500 Kbps
			},
		});
		const manifestUrl = 'https://storage.googleapis.com/shaka-demo-assets/angel-one/dash.mpd';

		player.load(manifestUrl)
			.then(() => {
				console.log('The video has now been loaded!');
			})
			.catch(onError);

		return () => {
			if (playerRef.current) {
				// Clean up the player and event listeners
				playerRef.current.removeEventListener('error', onErrorEvent);
				playerRef.current.destroy();
			}
		};
	}, [onErrorEvent, onError]);

	const handleVideoPlay = () => {
		if(!isPlaying) {
			setIsPlaying(true);
			videoRef.current.play();
		}
	};

	const handleVideoPause = () => {
		if(isPlaying) {
			setIsPlaying(false);
			videoRef.current.pause();
		}
	};

	return (
		<div className="video-container">
			<video
				className="shaka-video"
				ref={videoRef}
				onClick={isPlaying ? handleVideoPause : handleVideoPlay}
				autoPlay={true}
			/>
			<div className="controls-container">
				<ProgressBar player={playerRef.current} />
				<SkipButtons player={playerRef.current} />
				<div className="progress-bar" onClick={handleProgressBarClick}>
					<div className="progress" ref={progressRef} />
				</div>
				<div className="skip-buttons">
					<button className="skip-button" onClick={handleSkipBackward}>
						- 10s
					</button>
					<button className="skip-button" onClick={handleSkipForward}>
						+ 10s
					</button>
				</div>
			</div>
		</div>
	);
};

export default VideoPlayer;
