import React from 'react';

const SkipButtons = ({ videoRef }) => {
    const handleSkip = (time) => {
        if (videoRef) {
            videoRef.current.currentTime += time;
        }
    };

    return (
        <div className="skip-buttons">
            <button className="skip-button" onClick={() => handleSkip(-10)}> -10 </button>
            <button className="skip-button" onClick={() => handleSkip(10)}> +10 </button>
        </div>

    );
};

export default SkipButtons;
